import React, { useState } from "react";
import Mat from "./Mat.js";

export default function Matrix() {
  const [vertexCount, setVertexCount] = useState(5);

  function onChangeHandler(e) {
    let value = e.target.value;
    if (value < 2) {
      setVertexCount(2);
      return;
    }
    if (value > 10) {
      setVertexCount(10);
      return;
    }

    setVertexCount(value);
  }

  return (
    <div className="right">
      <label>
        Количество вершин графа:
        <input
          id="vertex_cont"
          type="number"
          defaultVValue={vertexCount}
          onChange={onChangeHandler}
        />
      </label>

      <button className="create_matrix_btn">Создать матрицу весов</button>

      <Mat count={vertexCount} />
    </div>
  );
}
