import React from "react";

function ContextMenu({ context, xyPosition, setContext, onDelete, onInsert }) {
  //function to set the chosen menu option
  const [chosen, setChosen] = React.useState();

  const initMenu = (chosen) => {
    console.log(chosen);
    setChosen(chosen);
    setContext(false);
  };

  //JavaScript XML (JSX) returned by the component
  return (
    <div className="contextContainer">
      {context && (
        <div
          style={{ top: xyPosition.y, left: xyPosition.x }}
          className="rightClick"
        >
          <div
            className="menuElement"
            onClick={() => {
              setContext(false);
              {
                onInsert;
              }
            }}
          >
            Добавить вершину
          </div>
          <div className="menuElement" onClick={onDelete}>
            Удалить вершину
          </div>
        </div>
      )}
    </div>
  );
}

export default ContextMenu;
