import React, { useRef } from "react";

export default function Output({ rowHeaders }) {
  const startSelectRef = useRef(null);
  console.log("Tools", rowHeaders);
  const newArr = rowHeaders.map((val, ind) => {
    console.log(ind); // Выводит текущее значение элемента массива
    return val; // Применяйте какие-то изменения; в этом случае умножаем на 2
  });
  console.log(newArr);

  return (
    <>
        <label for="start_vertex_select">Начальная вершина:</label>
        <select name="start_vertex" id="start_vertex_select">
          ref={startSelectRef}
          <option value="empty1"></option>;
          {rowHeaders.map((val, ind) => (
            <option value={ind}>{val}</option>
          ))}
        </select>
      <div className="end_select">
        <label for="end_vertex_select">Конечная вершина:</label>
        <select name="end_vertex" id="end_vertex_select">
          <option value="empty2"></option>;
          {rowHeaders
            .filter((val) => val !== startSelectRef.current.value)
            .map((val, ind) => (
              <option value={ind + 1}>{val}</option>
            ))}
        </select>
      </div>
    </>
  );
}
