import React from "react";

function VertexContextMenu({
  context,
  xyPosition,
  setContext,
  onDelete,
  onStart,
  onEnd,
  addArc,
  delArc,
  onDelArc,
  changeArc,
  del,
}) {
  const [subMenuVisible, setSubMenuVisible] = useState(false);

  return (
    <div className="contextContainer">
      {context && (
        <div
          style={{ top: xyPosition.y, left: xyPosition.x }}
          className="rightClick"
        >
          <div
            className="menuElement"
            onClick={() => {
              setContext(false);
              onStart();
            }}
          >
            Выбрать начальной
          </div>

          <div
            className="menuElement"
            onClick={() => {
              setContext(false);
              onEnd();
            }}
          >
            Выбрать конечной
          </div>
          {del && (
            <div
              className="menuElement"
              onClick={() => {
                setContext(false);
                onDelete();
              }}
            >
              Удалить вершину
            </div>
          )}
          {addArc && (
            <div
              className="menuElement"
              //   onClick={() => {
              //     setContext(false);
              //     onInsert();
              //   }}
            >
              Добавить ребро
            </div>
          )}
          {delArc && (
            <div
              className="menuElement"
                // onClick={() => {
                //   setContext(false);
                //   onDelArc();
                // }}
            >
              Удалить ребро
              {subMenuVisible && (
              <div className="rightClick" style={{ position: 'absolute', left: '100%', top: 0 }}>
                <div className="menuElement" onClick={() => {/* действия для первого подменю */}}>Подменю 1</div>
                <div className="menuElement" onClick={() => {/* действия для второго подменю */}}>Подменю 2</div>
              </div>
            )}
            </div>
          )}
          {changeArc && (
            <div
              className="menuElement"
              //   onClick={() => {
              //     setContext(false);
              //     onInsert();
              //   }}
            >
              Изменить вес ребра
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default VertexContextMenu;
