import React, { useState, useRef, useEffect } from "react";

function VertexContextMenu({
  context,
  xyPosition,
  setContext,
  onDelete,
  onStart,
  onEnd,
  addArc,
  delArc,
  onDelArc,
  changeArc,
  del,
  names,
}) {
  const [subMenuVisible, setSubMenuVisible] = useState(false);
  const [isHoveredOnPopup, setIsHoveredOnPopup] = useState(false);
  const [y, setY] = useState(null);
  const outerRef = useRef();

  return (
    <div className="contextContainer">
      {context && (
        <div
          ref={outerRef}
          style={{ top: xyPosition.y, left: xyPosition.x }}
          className="rightClick"
        >
          <div
            className="menuElement"
            onClick={() => {
              setContext(false);
              onStart();
            }}
          >
            Выбрать начальной
          </div>

          <div
            className="menuElement"
            onClick={() => {
              setContext(false);
              onEnd();
            }}
          >
            Выбрать конечной
          </div>
          {del && (
            <div
              className="menuElement"
              onClick={() => {
                setContext(false);
                onDelete();
              }}
            >
              Удалить вершину
            </div>
          )}
          {addArc && (
            <div
              className="menuElement"
              //   onClick={() => {
              //     setContext(false);
              //     onInsert();
              //   }}
            >
              Добавить ребро
            </div>
          )}
          {delArc && (
            <div
              className="menuElement"
              onMouseEnter={(e) => {
                setSubMenuVisible((prev) => {
                  const inner = e.target.getBoundingClientRect();
                  const outer = outerRef.current.getBoundingClientRect();
                  setY(inner.top - outer.top);
                  return true;
                });
              }}
              onMouseLeave={() => {
                if (!isHoveredOnPopup) {
                  setSubMenuVisible(false);
                }
              }}
            >
              Удалить ребро
              {subMenuVisible && (
                <div
                  className="rightClick"
                  style={{ position: "absolute", left: "100%", top: y }}                      onMouseEnter={handlePopupMouseEnter}
                      onMouseLeave={handlePopupMouseLeave}
                >
                  {names.map((n, ind) => (
                    <div
                      key={ind}
                      className="menuElement"
                      onClick={() => {
                        setSubMenuVisible(false);
                        setContext(false);
                        onDelArc(n);
                      }}

                    >
                      {n}
                    </div>
                  ))}
                </div>
              )}
            </div>
          )}
          {changeArc && (
            <div
              className="menuElement"
              //   onClick={() => {
              //     setContext(false);
              //     onInsert();
              //   }}
            >
              Изменить вес ребра
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default VertexContextMenu;
