import React from "react";

function CanvasContextMenu({
  context,
  xyPosition,
  setContext,
  onInsert,
  insert,
}) {
  return (
    <div className="contextContainer">
      {context && insert && (
        <div
          style={{ top: xyPosition.y, left: xyPosition.x }}
          className="rightClick"
        >
            <div
              className="menuElement"
              onClick={() => {
                setContext(false);
                onInsert();
              }}
            >
              Добавить вершину
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default CanvasContextMenu;
