import React, { useState, useRef } from "react";
import Matrix from "./Matrix.jsx";
import Header from "./Header.jsx";

export default function MatrixComponent() {
  const [vertexCount, setVertexCount] = useState(5);
  const input = useRef();

  const vertexNames = ["A", "B", "C", "D", "E", "F", "G", "H", "I", "J"];
  const [columnHeaders, setcolumnHeaders] = useState(
    vertexNames.slice(0, vertexCount)
  );
  const [rowHeaders, setrowHeaders] = useState(
    vertexNames.slice(0, vertexCount)
  );
  const [data, setData] = useState(
    [...Array(count)].map((e) => Array(count).fill(""))
  );

  // Обработчик изменения значения в ячейке
  const handleChange = (rowIndex, colIndex, value) => {
    const newData = data.map((row, rIdx) => {
      if (rIdx === rowIndex) {
        const newRow = [...row];
        newRow[colIndex] = value;
        return newRow;
      }
      return row;
    });
    setData(newData);
  };

  function onClickHandler() {
    let value = input.current.value;
    value = value < 2 ? 2 : value;
    value = value > 10 ? 10 : value;

    setVertexCount(value);


    input.current.value = value;
  }

  return (
    <>
      <Header
        input={input}
        vertexCount={vertexCount}
        onClickHandler={onClickHandler}
      />
      <Matrix columnHeaders={columnHeaders}rowHeaders={rowHeaders} data={data} onChange={handleChange}/>
    </>
  );
}
