import React, { useState, useRef } from "react";
import Mat from "./Mat.js";

export default function Matrix() {
  const [vertexCount, setVertexCount] = useState(5);
  const input = useRef();

  function onClickHandler() {
    let value = input.current.value;
    value = value < 2 ? 2 : value;
    value = value > 10 ? 10 : value;

    setVertexCount(value);
    input.current.value = value;
  }

  return (
    <>
      <label>
        Количество вершин графа:&emsp;
        <input
          id="vertex_cont"
          ref={input}
          type="number"
          min="2"
          max="10"
          defaultValue={vertexCount}
        />
      </label>
      <br />
      <button className="create_matrix_btn" onClick={onClickHandler}>
        Создать матрицу весов
      </button>

      <Mat count={vertexCount} />
    </>
  );
}
