import React, { useRef } from "react";
import ContextMenu from "./ContextMenu";

const Matrix = ({ columnHeaders, rowHeaders, data, onChange, onInput }) => {
  const rowRef = useRef(null);

  // Контекстное меню
  const [context, setContext] = React.useState(false);
  const [xyPosition, setxyPosition] = React.useState({ x: 0, y: 0 });
  //function to set the chosen menu option
  const [chosen, setChosen] = React.useState();
  const initMenu = (chosen) => {
    console.log(chosen);
    setChosen(chosen);
  };

  //event handler for showing the context menu
  const Context = (event) => {
    event.preventDefault();
    setContext(false);
    const positionChange = {
      x: event.pageX,
      y: event.pageY,
    };
    setxyPosition(positionChange);
    setContext(true);
  };

  //event handler for hiding the context menu
  const hideContext = (event) => {
    setContext(false);
  };

  // Курсор переводим в конец
  function cursorToEnd(e) {
    let range = document.createRange();
    range.selectNodeContents(e.target);
    range.collapse(false);
    let selection = window.getSelection();
    selection.removeAllRanges();
    selection.addRange(range);
  }

  function handleContextMenu(e) {
    e.preventDefault();
  }

  return (
    <>
      <table className="table">
        <thead>
          <tr>
            <th></th>
            {columnHeaders.map((header, index) => (
              <th key={index}>{header}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {data.map((row, rowIndex) => (
            <tr key={rowIndex} onContextMenu={(e) => handleContextMenu(e)}>
              <td
                id="row_header"
                ref={rowRef}
                contentEditable="true"
                suppressContentEditableWarning={true}
                onInput={(e) => {
                  onInput(e.target.innerText, rowIndex);
                  cursorToEnd(e);
                }}
              >
                {rowHeaders[rowIndex]}
              </td>
              {row.map((cell, colIndex) => (
                <td key={colIndex}>
                  <input
                    type="number"
                    value={cell}
                    disabled={rowIndex === colIndex ? true : false}
                    onChange={(e) =>
                      onChange(rowIndex, colIndex, e.target.value)
                    }
                  />
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
      <ContextMenu />
    </>
  );
};

export default Matrix;
