import React from "react";

function VertexContextMenu({
  context,
  xyPosition,
  setContext,
  onDelete,
  onStart,
  onEnd,
  addArc,
  delArc,
  changeArc,
  del,
}) {
  return (
    <div className="contextContainer">
      {context && (
        <div
          style={{ top: xyPosition.y, left: xyPosition.x }}
          className="rightClick"
        >
          <div
            className="menuElement"
            onClick={() => {
              setContext(false);
              onStart();
            }}
          >
            Выбрать начальной
          </div>

          <div
            className="menuElement"
            onClick={() => {
              setContext(false);
              onEnd();
            }}
          >
            Выбрать конечной
          </div>
          {del && (
            <div
              className="menuElement"
              onClick={() => {
                setContext(false);
                onDelete();
              }}
            >
              Удалить вершину
            </div>
          )}
          {addArc && (
            <div
              className="menuElement"
              //   onClick={() => {
              //     setContext(false);
              //     onInsert();
              //   }}
            >
              Добавить ребро
            </div>
          )}
          {delArc && (
            <div
              className="menuElement"
              //   onClick={() => {
              //     setContext(false);
              //     onInsert();
              //   }}
            >
              Удалить ребро
            </div>
          )}
          {changeArc && (
            <div
              className="menuElement"
              //   onClick={() => {
              //     setContext(false);
              //     onInsert();
              //   }}
            >
              Изменить вес ребра
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default VertexContextMenu;
