import React, { useEffect, useRef, useState } from "react";

export function ChangeWeightWindow({
  y,
  setIsHoveredOnChange,
  setSubChange,
  vertexes,
  setContext,
  onChangeArc,
}) {
  return (
    <div
      className="rightClick"
      style={{ position: "absolute", left: "97%", top: y }}
      onMouseEnter={() => setIsHoveredOnChange(true)}
      onMouseLeave={() => {
        setIsHoveredOnChange(false);
        setSubChange(false);
      }}
    >
      <div className="dropdown">
        <label htmlFor="change_weight">Вершина:&emsp;</label>
        <select id="change_weight" ref={changeWeightSelect}>
          {vertexes.map((item, index) => (
            <option key={index} value={item}>
              {item}
            </option>
          ))}
        </select>
      </div>
      <label className="change_weight_label">
        Вес ребра:&emsp;
        <input
          className="change_weight__unput"
          ref={changeWeightInput}
          type="number"
          min="0"
          onInput={(e) => {
            if (parseInt(e.target.value) < 0) {
              e.target.value = "";
            }
          }}
        />
      </label>
      <button
        onClick={(e) => {
          setSubChange(false);
          setContext(false);
          onChangeArc(
            changeWeightSelect.current.value,
            changeWeightInput.current.value
          );
        }}
        id="add_btn"
      >
        Добавить
      </button>
    </div>
  );
}
