import React, { useRef } from "react";

const Matrix = ({ columnHeaders, rowHeaders, data, onChange, onInput }) => {
  const rowRef = useRef(null);

  function cursorToEnd(e) {

    // Курсор переводим в конец
    let range = document.createRange();
    range.selectNodeContents(e.target);
    range.collapse(false);
    let selection = window.getSelection();
    selection.removeAllRanges();
    selection.addRange(range);
  }

  return (
    <table className="table">
      <thead>
        <tr>
          <th></th>
          {columnHeaders.map((header, index) => (
            <th key={index}>{header}</th>
          ))}
        </tr>
      </thead>
      <tbody>
        {data.map((row, rowIndex) => (
          <tr key={rowIndex} on>
            <td
              id="row_header"
              ref={rowRef}
              contentEditable="true"
              suppressContentEditableWarning={true}
              onInput={(e) => {
                onInput(e.target.innerText, rowIndex);
                cursorToEnd(e);
              }}
            >
              {rowHeaders[rowIndex]}
            </td>
            {row.map((cell, colIndex) => (
              <td key={colIndex}>
                <input
                  type="number"
                  value={cell}
                  disabled={rowIndex === colIndex ? true : false}
                  onChange={(e) => onChange(rowIndex, colIndex, e.target.value)}
                />
              </td>
            ))}
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default Matrix;
