import React, { useRef } from "react";

const Matrix = ({ columnHeaders, rowHeaders, data, onChange, onInput }) => {
  const rowRef = useRef(null);

  useEffect(() => {
    // Перемещаем курсор в конец текста
    const element = rowRef.current;
    if (element) {
      const range = document.createRange();
      const selection = window.getSelection();
      range.selectNodeContents(element);
      range.collapse(false); // перемещаем курсор в конец
      selection.removeAllRanges();
      selection.addRange(range);
    }
  }, [text]);

  return (
    <table className="table">
      <thead>
        <tr>
          <th></th>
          {columnHeaders.map((header, index) => (
            <th key={index}>{header}</th>
          ))}
        </tr>
      </thead>
      <tbody>
        {data.map((row, rowIndex) => (
          <tr key={rowIndex}>
            <td
              id="row_header"
              ref={rowRef}
              contentEditable="true"
              suppressContentEditableWarning={true}
              onInput={(e) => {
                console.log(e.target);
                onInput(e.target.innerText, rowIndex);
              }}
            >
              {rowHeaders[rowIndex]}
            </td>
            {row.map((cell, colIndex) => (
              <td key={colIndex}>
                <input
                  type="number"
                  value={cell}
                  disabled={rowIndex === colIndex ? true : false}
                  onChange={(e) => onChange(rowIndex, colIndex, e.target.value)}
                />
              </td>
            ))}
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default Matrix;
