import React, { useState, useRef, useEffect } from "react";

function VertexContextMenu({
  context,
  xyPosition,
  setContext,
  onDelete,
  onStart,
  onEnd,
  addArc,
  delArc,
  onDelArc,
  changeArc,
  del,
  names,
}) {
  const [y, setY] = useState(null);
  const outerRef = useRef();

  const [subDelete, setSubDelete] = useState(false);
  const [isHoveredOnDelete, setIsHoveredOnDelete] = useState(false);

  const [subChange, setSubChange] = useState(false);
  const [isHoveredOnChange, setIsHoveredOnChange] = useState(false);

  // const [subDelete, setSubDelete] = useState(false);
  // const [isHoveredOnDelete, setIsHoveredOnDelete] = useState(false);

  return (
    <div className="contextContainer">
      {context && (
        <div
          ref={outerRef}
          style={{ top: xyPosition.y, left: xyPosition.x }}
          className="rightClick"
        >
          <div
            className="menuElement"
            onClick={() => {
              setContext(false);
              onStart();
            }}
          >
            Выбрать начальной
          </div>

          <div
            className="menuElement"
            onClick={() => {
              setContext(false);
              onEnd();
            }}
          >
            Выбрать конечной
          </div>
          {del && (
            <div
              className="menuElement"
              onClick={() => {
                setContext(false);
                onDelete();
              }}
            >
              Удалить вершину
            </div>
          )}
          {addArc && (
            <div
              className="menuElement"
              //   onClick={() => {
              //     setContext(false);
              //     onInsert();
              //   }}
            >
              Добавить ребро
            </div>
          )}
          {delArc && (
            <div
              className="menuElement"
              onMouseEnter={(e) => {
                setSubDelete((prev) => {
                  const inner = e.target.getBoundingClientRect();
                  const outer = outerRef.current.getBoundingClientRect();
                  setY(inner.top - outer.top);
                  return true;
                });
              }}
              onMouseLeave={() => {
                if (!isHoveredOnDelete) {
                  setSubDelete(false);
                }
              }}
            >
              Удалить ребро
              {subDelete && (
                <div
                  className="rightClick"
                  style={{ position: "absolute", left: "97%", top: y }}
                  onMouseEnter={() => setIsHoveredOnDelete(true)}
                  onMouseLeave={() => {
                    setIsHoveredOnDelete(false);
                    setSubDelete(false);
                  }}
                >
                  {names.map((n, ind) => (
                    <div
                      key={ind}
                      className="menuElement"
                      onClick={() => {
                        setSubDelete(false);
                        setContext(false);
                        onDelArc(n);
                      }}
                    >
                      {n}
                    </div>
                  ))}
                </div>
              )}
            </div>
          )}
          {changeArc && (
            <div
              className="menuElement"
              //   onClick={() => {
              //     setContext(false);
              //     onInsert();
              //   }}
            >
              Изменить вес ребра
              {subChange && (
                <div
                  className="rightClick"
                  style={{ position: "absolute", left: "97%", top: y }}
                  onMouseEnter={() => setIsHoveredOnDelete(true)}
                  onMouseLeave={() => {
                    setIsHoveredOnDelete(false);
                    setSubDelete(false);
                  }}
                >
                  {names.map((n, ind) => (
                    <div
                      key={ind}
                      className="menuElement"
                      onClick={() => {
                        setSubDelete(false);
                        setContext(false);
                        onDelArc(n);
                      }}
                    >
                      {n}
                    </div>
                  ))}
                </div>
              )}

            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default VertexContextMenu;
