import React, { useState } from "react";

const Matrix = ({ count }) => {
  const vertexNames = ["A", "B", "C", "D", "E", "F", "G", "H", "I", "J"];

  // Заголовки столбцов и строк
  columnHeaders = vertexNames.slice(0, count);
  console.log(columnHeaders)
  const rowHeaders = [...columnHeaders];

  const [data, setData] = useState([...Array(6)].map((e) => Array(6).fill("")));

  // Обработчик изменения значения в ячейке
  const handleChange = (rowIndex, colIndex, value) => {
    const newData = data.map((row, rIdx) => {
      if (rIdx === rowIndex) {
        const newRow = [...row];
        newRow[colIndex] = value;
        return newRow;
      }
      return row;
    });
    setData(newData);
  };

  return (
    <table border="1">
      <thead>
        <tr>
          <th></th>
          {/* Пустая ячейка для заголовка строки */}
          {columnHeaders.map((header, index) => (
            <th key={index}>{header}</th>
          ))}
        </tr>
      </thead>
      <tbody>
        {data.map((row, rowIndex) => (
          <tr key={rowIndex}>
            <td>{rowHeaders[rowIndex]}</td>
            {row.map((cell, colIndex) => (
              <td key={colIndex}>
                <input
                  type="text"
                  value={cell}
                  onChange={(e) =>
                    handleChange(rowIndex, colIndex, e.target.value)
                  }
                />
              </td>
            ))}
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default Matrix;
