export function floyd(data) {
  console.log(data);
  data = data.map((r, i)=>r.map(v,j))

  const n = data.length;
  let D = Array.from({ length: n }, () =>
    Array.from({ length: n }, () => Infinity)
  );

  for (let i = 0; i < n; i++) {
    for (let j = 0; j < n; j++) {
      if ( i === j) D[i][j] = 0;
      else D[i][j] = data[i][j];
    }
  }

  for (let k = 0; k < n; k++) {
    for (let i = 0; i < n; i++) {
      for (let j = 0; j < n; j++) {
        D[i][j] = Math.min(D[i][j], D[i][k] + D[k][j])
      }
    }
  }

  console.log(D);
