import React, { useState } from "react";

const Matrix = ({ columnHeaders, rowHeaders, data, onChange}) => {
  const vertexNames = ["A", "B", "C", "D", "E", "F", "G", "H", "I", "J"];

  // // Заголовки столбцов и строк
  // let columnHeaders = vertexNames.slice(0, count);
  // let rowHeaders = [...columnHeaders];

  // const [data, setData] = useState(
  //   [...Array(count)].map((e) => Array(count).fill(""))
  // );
  // console.log(columnHeaders, rowHeaders, data);
  // // Обработчик изменения значения в ячейке
  // const handleChange = (rowIndex, colIndex, value) => {
  //   const newData = data.map((row, rIdx) => {
  //     if (rIdx === rowIndex) {
  //       const newRow = [...row];
  //       newRow[colIndex] = value;
  //       return newRow;
  //     }
  //     return row;
  //   });
  //   setData(newData);
  // };

  return (
    <table border="1">
      <thead>
        <tr>
          <th></th>
          {/* Пустая ячейка для заголовка строки */}
          {columnHeaders.map((header, index) => (
            <th key={index}>{header}</th>
          ))}
        </tr>
      </thead>
      <tbody>
        {data.map((row, rowIndex) => (
          <tr key={rowIndex}>
            <td>{rowHeaders[rowIndex]}</td>
            {row.map((cell, colIndex) => (
              <td key={colIndex}>
                <input
                  type="text"
                  value={cell}
                  disabled={rowIndex === colIndex ? true : false}
                  onChange={(e) =>
                    on(rowIndex, colIndex, e.target.value)
                  }
                />
              </td>
            ))}
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default Matrix;
