import React, { useEffect, useRef, useState } from "react";

export default function ChangeWeightWindow({
  y,
  setIsHoveredOnChange,
  setSubChange,
  vertexes,
  setContext,
  onChangeArc,
  changeWeightInput,
  changeWeightSelect,
}) {
  return (
    <div className="contextContainer">
      <div
        className="rightClick"
        id="change_box"
        style={{ position: "absolute", left: "97%", top: y }}
        onMouseEnter={() => setIsHoveredOnChange(true)}
        onMouseLeave={() => {
          setIsHoveredOnChange(false);
          setSubChange(false);
        }}
      >
        <div className="dropdown">
          <label htmlFor="change_weight">Вершина:&emsp;</label>
          <select id="change_weight" ref={changeWeightSelect}>
            {vertexes.map((item, index) => (
              <option key={index} value={item}>
                {item}
              </option>
            ))}
          </select>
        </div>
        <div></div>
        <label htmlFor="change_weight_input">
          Вес ребра:&emsp;</label>
          <input
            className="change_weight_input"
            ref={changeWeightInput}
            type="number"
            min="0"
            onInput={(e) => {
              if (parseInt(e.target.value) < 0) {
                e.target.value = "";
              }
            }}
          />
        
        <button
          onClick={(e) => {
            setSubChange(false);
            setContext(false);
            onChangeArc(
              changeWeightSelect.current.value,
              changeWeightInput.current.value
            );
          }}
          id="add_btn"
        >
          Добавить
        </button>
      </div>
    </div>
  );
}
