import React from "react";

export default function Header() {
  const [vertexCount, setVertexCount] = useState(5);

  function onCreateMatrixBtnClick() {
    let input = document.getElementById("vertex_cont");
    let count = input.value;

    if (count < 2) {
      input.value = 2;
    }
    if (count > 10) {
      input.value = 10;
    }
  }

  return (
    <div className="right">
      <label>
        Количество вершин графа:
        <input
          id="vertex_cont"
          type="number"
          defaultValue={vertexCount}
          onChange={(e) => setVertexCount(e.target.value)}
        />
      </label>

      <button className="create_matrix_btn" onClick={onCreateMatrixBtnClick}>
        Создать матрицу весов
      </button>
    </div>
  );
}
