import React, { useRef, useState } from "react";
import { findMinPath } from "../../algorithms/dijkstra.js";

export default function Output({ rowHeaders, data }) {
  const startSelectRef = useRef();
  const endSelectRef = useRef();
  const [length, setLength] = useState(null);
  const filteredItems = rowHeaders.filter((item) => item.trim() !== "");

  function handleClick() {
    console.log(data, rowHeaders.indexOf(startSelectRef.current.value));
    const x = data.map((v) => v.map(parseInt(v) ? parseInt(v) : 0));
    console.log(x)

    // let [len, path] = findMinPath(
    //   data,
    //   startSelectRef.current.value,
    //   endSelectRef.current.value
    // );
    // console.log(len, path);
  }

  return (
    <div>
      <div className="dropdown">
        <label htmlFor="dropdown1">Начальная вершина:&emsp;</label>
        <select id="dropdown1" ref={startSelectRef}>
          {filteredItems.map((item, index) => (
            <option key={index} value={item}>
              {item}
            </option>
          ))}
        </select>
      </div>
      <div className="dropdown">
        <label htmlFor="dropdown2">Конечная вершина:&emsp;</label>
        <select id="dropdown2" ref={endSelectRef}>
          {filteredItems
            .filter((val) => val !== startSelectRef.current.value)
            .map((item, index) => (
              <option key={index} value={item}>
                {item}
              </option>
            ))}
        </select>
      </div>
      <br />
      <button className="dijkstra_btn" id="btn" onClick={handleClick}>
        Найти кратчайший путь
      </button>
      <hr className="hr" />
      <p id="result">
        Длина пути:&emsp;<span>{length}</span>
      </p>
      <p id="result">
        Список вершин:&emsp;<span>{length}</span>
      </p>
    </div>
  );
}
