import React, { useState } from "react";

// export default function Math({ count }) {
//   console.log(count);
//   return <h1>{count}</h1>;
// }

const EditableTable = ({count}) => {
  // Начальные данные для таблицы
  const initialData = [
    ['Row 1 Col 1', 'Row 1 Col 2', 'Row 1 Col 3'],
    ['Row 2 Col 1', 'Row 2 Col 2', 'Row 2 Col 3'],
    ['Row 3 Col 1', 'Row 3 Col 2', 'Row 3 Col 3'],
  ];

  const vertexNames = ["A", "B", "C", "D", "E", "F", "G", "H",]

  // Заголовки столбцов и строк
  const columnHeaders = ['Header 1', 'Header 2', 'Header 3'];
  const rowHeaders = ['Row 1', 'Row 2', 'Row 3'];

  const [data, setData] = useState(initialData);

  // Обработчик изменения значения в ячейке
  const handleChange = (rowIndex, colIndex, value) => {
    const newData = data.map((row, rIdx) => {
      if (rIdx === rowIndex) {
        const newRow = [...row];
        newRow[colIndex] = value;
        return newRow;
      }
      return row;
    });
    setData(newData);
  };

  return (
    <table border="1">
      <thead>
        <tr>
          <th></th>{/* Пустая ячейка для заголовка строки */}
          {columnHeaders.map((header, index) => (
            <th key={index}>{header}</th>
          ))}
        </tr>
      </thead>
      <tbody>
        {data.map((row, rowIndex) => (
          <tr key={rowIndex}>
            <td>{rowHeaders[rowIndex]}</td>
            {row.map((cell, colIndex) => (
              <td key={colIndex}>
                <input
                  type="text"
                  value={cell}
                  onChange={(e) => handleChange(rowIndex, colIndex, e.target.value)}
                />
              </td>
            ))}
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default EditableTable;
